#!/usr/bin/env python3# -*- coding: utf-8 -*-"""Created on Sat Jan 28 01:52:09 2020@author: Gustavo Borges Moreno e Mello@email: gmoreno.mello@gmail.com"""import cv2 as cvimport osimport globfrom os import listdirfrom os.path import isfile, joinif os.name == 'nt':    import win32api, win32con#cwd = os.getcwd()from file_handler import *def resize_all_images(img_list, cwd, target_width=500):    """    Resizes all image in a list to a specific width    Parameters    ----------    img_list : Listo of strings        LIST WITH ALL THE FILES IN THE UNIFIED SYSTEM RESOURCES    cwd : STRING        Relative path to the USR folder.    target_width : INT, optional        Desired size of the width of the resized image. The default is 500.    Returns    -------    None.    """    for p in img_list:        full_path = os.path.join(cwd,p)        img = cv.imread(full_path)        dims = img.shape        scale_factor = target_width / dims[1]        target_height = int(scale_factor * dims[0])        rz_img = cv.resize(img, (target_width,target_height))        #encode_param = [int(cv.IMWRITE_JPEG_QUALITY), 90]        #result, encimg = cv.imencode('.jpg', img, encode_param)        cv.imwrite(full_path,rz_img)if __name__ == '__main__':    cwd = os.path.join('..','usr')    img_list = list_files_in_resources(cwd)    img_list.sort()    resize_all_images(img_list, cwd)    print("oi")